import React from 'react';

import YouTube from 'react-youtube';

import './TrailerPreview.scss';

function TrailerPreview(props)  {     
    let height = window.innerWidth * 0.7 * 9 / 16
    let style = {
        height: height
    }

    let opts = {
        height: height,   // aspect ratio 16:9
        width: window.innerWidth * 0.7,     // width: 70% 
        playerVars: {
            autoplay: 0,
            controls: 1,

        }
    }



    return (
        <div className="preview">
            <div className="preview-bg">
                <div className="preview-bg-shadow" style={{height: height+"px"}}>
                    123
                </div>
                <YouTube 
                // https://www.npmjs.com/package/react-youtube
                className="preview-player"
                videoId="jL_oiGebKM4"
                opts={opts}
                />
            </div>


        </div>
    )

}

export default TrailerPreview;



// Animated description window

function animatedDescription(props) {

    return (
        <div className="preview-description">
            <h3>{props.title}</h3>
            <h4>{props.rating}</h4>
            <p>{props.description}</p>
            <ul>
                {props.actors.map(a => <li>{a}</li>)}
            </ul>
        </div>
    )
}
