import React, { Component } from 'react';

import { CarouselProvider, Slider, Slide, ButtonBack, ButtonNext} from 'pure-react-carousel';
import 'pure-react-carousel/dist/react-carousel.es.css';

import './Carousel.scss';


class Carousel extends Component {
    constructor(props) {
        super(props);

        this.state = {
            children: []
        }
    }

    componentDidMount() {
        this.setState({children : this.props.children})
    }



    render() {
        let parts = splitArray(this.state.children);

        return (
            <CarouselProvider
                naturalSlideHeight={25}
                naturalSlideWidth={100}
                totalSlides={parts.length}
            >
                <Slider>
                {parts.map((part, i) => {

                        return (
                            <Slide index={i} innerClassName="carousel-group">
                                {part.map( poster => poster)}
                            </Slide>
                        )
                    })}
                </Slider>
                <ButtonBack className="carousel-btn back">Back</ButtonBack>
                <ButtonNext className="carousel-btn next">Next</ButtonNext>
            </CarouselProvider>
        )
    }
}

function splitArray(arr) {
    let parts = [];
    let part = []

    for(let i = 0; i < arr.length; i++) {
        part.push(arr[i]);

        if((i + 1) % 5 === 0 ) {
            parts.push(part);
            part = [];
        }
    }

    return parts;
}


export default Carousel;
