import React from 'react';

import YouTube from 'react-youtube';



function TrailerPreview(props)  {  
    props.title="Title"
    this.props.rating="5.4"
    this.props.description="Little description"
    this.props.actors=["Me", "You"]

    let opts = {
        playerVars: {
            autoplay: 0,
            controls: 0,

        }
    }

    return (
        <div className="preview">
            <YouTube 
            // https://www.npmjs.com/package/react-youtube
            videoId="l96G1AF2S0U"
            opts={opts}
            />

            {animatedDescription(props)}
        </div>
    )

}

export default TrailerPreview;



// Animated description window

function animatedDescription(props) {

    return (
        <div className="preview-description">
            <h3>{props.title}</h3>
            <h4>{props.rating}</h4>
            <p>{props.description}</p>
            <ul>
                {props.actors.map(a => <li>{a}</li>)}
            </ul>
        </div>
    )
}
