import React, { Component } from 'react';

import Navbar from './components/Home/Navbar/Navbar';

import MoviePoster from './components/Home/MoviePoster/MoviePoster';
import Carousel from './components/Home/Carousel/Carousel';
import  Container from './components/Home/Container/Container';



import './App.scss';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
        favourites : []
    }
  }

  async componentDidMount() {
    let rawData = await fetch('http://localhost:6969/getFavFavourites');
    let data = await rawData.json();
    this.setState({ favourites: data })
  }

  render() {
    let arr = []
    for(let i = 0; i < 25; i++) {
      arr.push(<MoviePoster/>);
    }


    return (
      <div className="App">
        <Navbar />

        <Container title={"Fan Favourites"}>
        <Carousel children={arr} />
        </Container>
        <Container title={"Fan Favourites"}>
        <Carousel children={arr} />
        </Container>
        <Container title={"Fan Favourites"}>
        <Carousel children={arr} />
        </Container>
      </div>
    )
  }
}

export default App;
