import React from 'react';


import './MoviePoster.scss';



function MoviePoster(props) {
    let title = props.title;
    let genres = props.genres;
    let rating = props.rating;
    let poster = props.poster;
    let id = props.id;

    return (
        <div className="poster" onClick={() => props.onClickHandler(id)}>
            <div className="poster-image">
                <img src={poster} alt="poster"/>
            </div>
            <div className="poster-info">
                <div className="poster-info-name">
                    <span>{title}</span> &nbsp; <span>{rating}</span>
                </div>
                <div className="poster-info-genre">
                    <ul>
                    {genres.map((genre, i) => <li key={i}>{genre}</li>)}
                    </ul>
                </div>
            </div>
        </div>
    )
}

export default MoviePoster;
