import React from 'react';

function Suggestions(props) {
    if(!props.show){
        return null;
    } else {
    return(
        <div className="suggestions" ref={props.ref}>
            <ul className="list">
                {props.list.map((movie, i) => {
                    return (
                    <li key={i}>
                    <p className="item-year">{movie.release_date.slice(0,4) ? this : null}</p>
                    <p className="item-title">{movie.title}</p>
                    </li>
                    )
                })}
            </ul>
        </div>
    )
            }
}

export default Suggestions;
